svg.script:
//
  const svgContainer = document.getElementById('svgContainer');

  fetch('https://github-readme-stats.vercel.app/api/top-langs/?username=VLstack&layout=donut&hide_title=true&count_private=true&hide_border=true')
    .then(response => response.text())
    .then(svgText => {
      const parser = new DOMParser();
      const foreignDoc = parser.parseFromString(svgText, 'image/svg+xml');
      const svgElement = foreignDoc.documentElement;

      // Set the SVG element's width and height attributes
      svgElement.setAttribute('width', '100%');
      svgElement.setAttribute('height', '100%');

      svgContainer.innerHTML = ''; // Clear the "Loading..." message
      svgContainer.appendChild(svgElement);
    })
    .catch(error => {
      svgContainer.textContent = 'Error loading SVG';
      console.error('Error:', error);
    });
//
